"use strict";

var _wasmCrypto = require("@polkadot/wasm-crypto");

var _ = require(".");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('deriveAddress', () => {
  beforeEach(async () => {
    await (0, _wasmCrypto.waitReady)();
  });
  it('derives a known path', () => {
    expect((0, _.deriveAddress)('5CZtJLXtVzrBJq1fMWfywDa6XuRwXekGdShPR4b8i9GWSbzB', '/joe/polkadot/0')).toEqual('5GZ4srnepXvdsuNVoxCGyVZd8ScDm4gkGLTKuaGARy9akjTa');
  });
  it('fails on hard paths', () => {
    expect(() => (0, _.deriveAddress)('5CZtJLXtVzrBJq1fMWfywDa6XuRwXekGdShPR4b8i9GWSbzB', '//bob')).toThrow(/Expected suri to contain a combination of non-hard paths/);
  });
});