"use strict";

var _testingPairs = require("@polkadot/keyring/testingPairs");

var _ = require(".");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('addressEq', () => {
  it('returns false with non-equal', () => {
    expect((0, _.addressEq)('5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY', '5EnxxUmEbw8DkENKiYuZ1DwQuMoB2UWEQJZZXrTsxoz7SpgG')).toEqual(false);
  });
  it('returns true for equal, matching prefix', () => {
    expect((0, _.addressEq)('5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY', '5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY')).toEqual(true);
  });
  it('returns true for equal, non-matching prefix', () => {
    expect((0, _.addressEq)('5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY', '7sL6eNJj5ZGV5cn3hhV2deRUsivXfBfMH76wCALCqWj1EKzv')).toEqual(true);
  });
  it('returns true for equal, address vs publicKey', () => {
    const keyring = (0, _testingPairs.createTestPairs)({
      type: 'sr25519'
    });
    expect((0, _.addressEq)('5GrwvaEF5zXb26Fz9rcQpDWS57CtERHpNehXCPcNoHGKutQY', keyring.alice.publicKey)).toEqual(true);
  });
});