"use strict";

var _base = require("../base58");

var _ = require("./");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('base32Encode', () => {
  it('encodes to a base32', () => {
    expect((0, _.base32Encode)('Decentralize everything!!')).toEqual('irswgzloorzgc3djpjssazlwmvzhs5dinfxgoijb');
  });
  it('encodes a base58 to a base32', () => {
    expect((0, _.base32Encode)((0, _base.base58Decode)('zb2rhk6GMPQF3hfzwXTaNYFLKomMeC6UXdUt6jZKPpeVirLtV', true), true)).toEqual('bafkreigh2akiscaildcqabsyg3dfr6chu3fgpregiymsck7e7aqa4s52zy');
  });
});