"use strict";

var _util = require("@polkadot/util");

var _wasmCrypto = require("@polkadot/wasm-crypto");

var _ = require("./");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
const MENMONIC = 'seed sock milk update focus rotate barely fade car face mechanic mercy';
const SEED = '0x3c121e20de068083b49c2315697fb59a2d9e8643c24e5ea7628132c58969a027';
describe('mnemonicToLegacySeed', () => {
  beforeEach(async () => {
    await (0, _wasmCrypto.waitReady)();
  });
  [false, true].forEach(onlyJs => {
    it(`generates a valid seed (onlyJs = ${onlyJs.toString()})`, () => {
      expect((0, _util.u8aToHex)((0, _.mnemonicToLegacySeed)(MENMONIC, undefined, onlyJs))).toEqual(SEED);
    });
  });
});