"use strict";

var _util = require("@polkadot/util");

var _wasmCrypto = require("@polkadot/wasm-crypto");

var _asU8a = require("../random/asU8a");

var _fromSeed = require("./keypair/fromSeed");

var _sign = require("./sign");

var _verify = require("./verify");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
const MESSAGE = (0, _util.stringToU8a)('this is a message');
describe('sign and verify', () => {
  beforeEach(async () => {
    await (0, _wasmCrypto.waitReady)();
  });
  it('has 64-byte signatures', () => {
    const pair = (0, _fromSeed.schnorrkelKeypairFromSeed)((0, _asU8a.randomAsU8a)());
    expect((0, _sign.schnorrkelSign)(MESSAGE, pair)).toHaveLength(64);
  });
  it('can sign and verify a message', () => {
    const pair = (0, _fromSeed.schnorrkelKeypairFromSeed)((0, _asU8a.randomAsU8a)());
    const signature = (0, _sign.schnorrkelSign)(MESSAGE, pair);
    expect((0, _verify.schnorrkelVerify)(MESSAGE, signature, pair.publicKey)).toBe(true);
  });
  it('throws error when publicKey lengths do not match', () => {
    expect(() => (0, _verify.schnorrkelVerify)(new Uint8Array([0x61, 0x62, 0x63, 0x64]), new Uint8Array(64), new Uint8Array(31))).toThrow(/Invalid publicKey/);
  });
  it('throws error when signature lengths do not match', () => {
    expect(() => (0, _verify.schnorrkelVerify)(new Uint8Array([0x61, 0x62, 0x63, 0x64]), new Uint8Array(65), new Uint8Array(32))).toThrow(/Invalid signature/);
  });
});