"use strict";

var _util = require("@polkadot/util");

var _wasmCrypto = require("@polkadot/wasm-crypto");

var _ = require(".");

// Copyright 2017-2020 @polkadot/util-crypto authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('xxhashAsU8a', () => {
  beforeEach(async () => {
    await (0, _wasmCrypto.waitReady)();
  });
  [false, true].forEach(onlyJs => {
    it(`returns a 64-bit value by default (onlyJs=${onlyJs.toString()})`, () => {
      expect((0, _.xxhashAsU8a)('abc', undefined, onlyJs)).toEqual((0, _util.hexToU8a)('0x990977adf52cbc44'));
    });
  });
  [false, true].forEach(onlyJs => {
    it(`returns a 128-bit value (as specified, onlyJs=${onlyJs.toString()})`, () => {
      expect((0, _.xxhashAsU8a)('abc', 128, onlyJs)).toEqual((0, _util.hexToU8a)('0x990977adf52cbc440889329981caa9be'));
    });
  });
  [false, true].forEach(onlyJs => {
    it(`returns a 256-bit value (as specified, onlyJs=${onlyJs.toString()})`, () => {
      expect((0, _.xxhashAsU8a)('abc', 256, onlyJs)).toEqual((0, _util.hexToU8a)('0x990977adf52cbc440889329981caa9bef7da5770b2b8a05303b75d95360dd62b'));
    });
  });
});