describe('bnSqrt', () => {
  it('fails on < 0 roots', () => {
    expect(() => (0, _.bnSqrt)(new _bn.default(-1))).toThrow(/negative numbers is not supported/);
  });
  it('finds the sqrt (exact)', () => {
    expect((0, _.bnSqrt)(new _bn.default(256).mul(new _bn.default(256)))).toEqual(new _bn.default(256));
  });
  it('finds the sqrt (round)', () => {
    expect((0, _.bnSqrt)(new _bn.default(266))).toEqual(new _bn.default(16));
  });
  it('find the sqrt for 1', () => {
    expect((0, _.bnSqrt)(new _bn.default(1))).toEqual(new _bn.default(1));
  });
});