"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('isAscii', () => {
  it('returns true for an ASCII string', () => {
    expect((0, _.isAscii)('Hello\tWorld!\n\rTesting')).toEqual(true);
  });
  it('returns true for ASCII bytes', () => {
    expect((0, _.isAscii)(new Uint8Array([0x31, 0x32, 0x20, 10]))).toEqual(true);
  });
  it('returns true for empty string inputs', () => {
    expect((0, _.isAscii)('')).toEqual(true);
  });
  it('returns true for empty U8a inputs', () => {
    expect((0, _.isAscii)(new Uint8Array())).toEqual(true);
  });
  it('returns false for empty undefined inputs', () => {
    expect((0, _.isAscii)()).toEqual(false);
  });
  it('returns false for non-printable characters', () => {
    expect((0, _.isAscii)(new Uint8Array([5]))).toEqual(false);
  });
});