"use strict";

var _ = require(".");

// Copyright 2017-2020 @polkadot/util authors & contributors
// SPDX-License-Identifier: Apache-2.0
describe('u8aEq', () => {
  it('returns false when the lengths do not match', () => {
    expect((0, _.u8aEq)(new Uint8Array([1, 2, 3]), new Uint8Array([1, 2]))).toEqual(false);
  });
  it('returns false when the contents do not match', () => {
    expect((0, _.u8aEq)(new Uint8Array([1, 2, 3]), new Uint8Array([1, 2, 4]))).toEqual(false);
  });
  it('returns true when the contents do match', () => {
    expect((0, _.u8aEq)(new Uint8Array([1, 2, 3]), new Uint8Array([1, 2, 3]))).toEqual(true);
  });
  it('returns true with equalvalent hex & Uint8array', () => {
    expect((0, _.u8aEq)('0x010203', new Uint8Array([1, 2, 3]))).toEqual(true);
  });
});